window.onload=function(){(async function(){let e=$("#readgcs").attr("bucket"),t=$("#readgcs").attr("prefix");var a=document.getElementById("simple-gallery");let o="",i=await fetch(`https://storage.googleapis.com/storage/v1/b/${e}/o?prefix=${t}`);i.ok&&(await i.json()).items.forEach((t=>{o+=`\n        <a href="https://storage.googleapis.com/${e}/${t.name}" target="_blank">\n          <div class="box fancy-figure caption-position-bottom caption-effect-fade">\n            <figure itemprop="associatedMedia" itemscope itemtype="http://schema.org/ImageObject">\n              <div class="img">\n                <img src="https://www.mccurdyc.dev/${t.name}?quality=30&pad=100&bg-color=ffffff"/>\n              </div>\n              <figcaption>\n                <p>${t.name}</p>\n              </figcaption>\n            </figure>\n          </div>\n        </a>`})),a.innerHTML=o})().catch(console.error)};